def action_num_to_arrow(action_num):
	if (action_num == 0):
		# right arrow 
		return ">"
	elif (action_num == 1):
		# left arrow 
		return "<"
	elif  (action_num == 2):
		# up arrow
		return "^"
	elif (action_num == 3):
		# down arrow 
		return "V"
	elif (action_num == 4):
		# theta up 
		return "T"
	elif (action_num == 5):
		# theta down 
		return "t"
	else:
		print("THERE WAS AN ERROR")

